import React from "react";

const galleryImages = [
  {
    src: "https://images.pexels.com/photos/8422206/pexels-photo-8422206.jpeg",
    alt: "Children playing with toys in a classroom",
    label: "Play & Learning"
  },
  {
    src: "https://images.pexels.com/photos/8422241/pexels-photo-8422241.jpeg",
    alt: "Teacher reading a book to kids",
    label: "Story Time"
  },
  {
    src: "https://images.pexels.com/photos/3661392/pexels-photo-3661392.jpeg",
    alt: "Kids painting and doing art activities",
    label: "Creative Art"
  },
  {
    src: "https://images.pexels.com/photos/3662833/pexels-photo-3662833.jpeg",
    alt: "Children playing on a soft mat",
    label: "Safe Play Area"
  },
  {
    src: "https://images.pexels.com/photos/3661391/pexels-photo-3661391.jpeg",
    alt: "Child building with blocks",
    label: "Fine Motor Skills"
  },
  {
    src: "https://images.pexels.com/photos/8612922/pexels-photo-8612922.jpeg",
    alt: "Toddler playing at a table",
    label: "Toddler Corner"
  }
];

const Gallery = () => {
  return (
    <div className="max-w-6xl mx-auto px-4 py-10">
      <div className="text-center mb-10">
        <h1 className="text-3xl md:text-4xl font-bold mb-3">
          Our Photo Gallery
        </h1>
        <p className="text-gray-600 max-w-2xl mx-auto">
          Take a peek inside Elsie&apos;s Childcare &amp; Learning Center.
          Our spaces are designed to be warm, safe, and engaging for every child.
        </p>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {galleryImages.map((image, index) => (
          <div
            key={index}
            className="group rounded-2xl overflow-hidden shadow-md bg-white"
          >
            <div className="overflow-hidden">
              <img
                src={image.src}
                alt={image.alt}
                className="w-full h-56 object-cover transform group-hover:scale-105 transition-transform duration-300"
              />
            </div>
            <div className="p-4 flex items-center justify-between">
              <p className="font-semibold text-gray-800">{image.label}</p>
              <span className="text-xs px-2 py-1 rounded-full bg-indigo-50 text-indigo-600">
                Elsie&apos;s Care
              </span>
            </div>
          </div>
        ))}
      </div>

      <p className="text-center text-gray-500 mt-8 text-sm">
        *You can replace these sample photos later with real pictures of your classrooms,
        play areas, and staff.
      </p>
    </div>
  );
};

export default Gallery;
